mapper.update:
UPDATE client
   SET enabled = 'N'
 WHERE id = #{id}

UPDATE client
   SET ceo_name = #{ceo_name}, register_num = #{register_num}, address = #{address}, detail_address = #{detail_address}, zip_code = #{zip_code}, phone = #{phone}, email = #{email}
 WHERE id = #{id}
   AND enabled = #{enabled}

UPDATE item
   SET name = #{name}, category = #{category}, price = #{price}
 WHERE id = #{id}
